import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import "../AdminDashboard/admin.css";

const Admin = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [accountData, setAccountData] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [accountNumber, setAccountNumber] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const { adminJWT } = location.state || {};
    if (adminJWT) {
      localStorage.setItem("adminToken", adminJWT);
    }

    const token = localStorage.getItem("adminToken");
    if (!token) {
      navigate("/admin-login");
    }
  }, [location, navigate]);

  const getAuthHeader = () => {
    const token = localStorage.getItem("adminToken");
    return { Authorization: `Bearer ${token}` };
  };

  const fetchUserDetails = async () => {
    setLoading(true);
    setError("");
    try {
      // Fetch account data first
      const accountResponse = await axios.get(
        `http://localhost:8080/api/account/${accountNumber}`,
        { headers: getAuthHeader() }
      );
      setAccountData(accountResponse.data);

      // Use the userId from the account data to fetch user details
      const userResponse = await axios.get(
        `http://localhost:8080/api/users/${accountResponse.data.userId}`,
        { headers: getAuthHeader() }
      );
      setUserDetails(userResponse.data);
      console.log(accountResponse.data.userId);

      // Fetch transactions
      const transactionsResponse = await axios.get(
        `http://localhost:8080/api/transaction/${accountResponse.data.userId}`,
        { headers: getAuthHeader() }
      );
      setTransactions(transactionsResponse.data);

      // Store userId in sessionStorage (if needed for other operations)
      sessionStorage.setItem("currentUserId", accountResponse.data.userId);
    } catch (error) {
      console.error("Error fetching user details:", error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError("Failed to fetch user details. Please try again.");
      }
      setUserDetails(null);
      setAccountData(null);
      setTransactions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async () => {
    try {
      await axios.delete(
        `http://localhost:8080/api/admin/user/${userDetails.userId}`,
        { headers: getAuthHeader() }
      );
      alert("User deleted successfully");
      setUserDetails(null);
      setAccountData(null);
      setTransactions([]);
      setAccountNumber("");
    } catch (error) {
      console.error("Error deleting user:", error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError("Failed to delete user. Please try again.");
      }
    }
  };

  const handleLoanAction = async (action) => {
    try {
      await axios.post(
        `http://localhost:8080/api/admin/loan/${userDetails.loanId}`,
        { action },
        { headers: getAuthHeader() }
      );
      fetchUserDetails(); // Refresh user details after loan action
    } catch (error) {
      console.error(`Error ${action} loan:`, error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError(`Failed to ${action} loan. Please try again.`);
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    sessionStorage.removeItem("currentUserId");
    navigate("/");
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-dashboard-container">
        <header className="admin-dashboard-header">
          <span className="admin-dashboard-logo">Easy Banking</span>
          <h1 className="admin-dashboard-title">Admin Dashboard</h1>
          <div className="admin-user-info">
            <span className="admin-username">Admin</span>
            <span className="admin-user-avatar">👤</span>
          </div>
        </header>

        <div className="admin-dashboard-content">
          <nav className="admin-dashboard-nav">
            <button onClick={() => navigate("/admin-dashboard")}>
              Dashboard
            </button>
            <button onClick={() => navigate("/admin-users")}>
              Manage Users
            </button>
            <button onClick={() => navigate("/admin-loans")}>
              Manage Loans
            </button>
            <button onClick={() => navigate("/admin-reports")}>Reports</button>
            <button onClick={handleLogout}>Log out</button>
          </nav>

          <main className="admin-dashboard-main">
            <section className="admin-user-search">
              <h2>User Search</h2>
              <div className="admin-search-input">
                <input
                  type="text"
                  value={accountNumber}
                  onChange={(e) => setAccountNumber(e.target.value)}
                  placeholder="Enter Account Number"
                />
                <button onClick={fetchUserDetails} disabled={loading}>
                  {loading ? "Searching..." : "Search"}
                </button>
              </div>
              {error && <p className="error-message">{error}</p>}
            </section>

            {userDetails && accountData && (
              <section className="admin-user-details">
                <h2>User Details</h2>
                <div className="user-info">
                  <p>
                    <strong>Name:</strong> {userDetails.first_Name}{" "}
                    {userDetails.last_Name}
                  </p>
                  <p>
                    <strong>Account Number:</strong> {accountData.accNumber}
                  </p>
                  <p>
                    <strong>Account Balance:</strong> ${accountData.accBalance}
                  </p>
                  <p>
                    <strong>Email:</strong> {userDetails.email}
                  </p>
                  <p>
                    <strong>Account Type:</strong> {accountData.accType}
                  </p>
                  <p>
                    <strong>Account Created:</strong> {accountData.accCreatedAt}
                  </p>
                </div>

                <div className="admin-actions">
                  <button
                    onClick={handleDeleteUser}
                    className="delete-user-btn"
                  >
                    Delete User
                  </button>
                </div>

                {/* Add loan details and actions here if available */}

                <div className="transaction-history">
                  <h3>Transaction History</h3>
                  <table>
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Description</th>
                        <th>Amount</th>
                        <th>Balance</th>
                      </tr>
                    </thead>
                    <tbody>
                      {transactions.length > 0 ? (
                        transactions.map((transaction, index) => (
                          <tr key={index}>
                            <td>{transaction.transactionDate}</td>
                            <td>{transaction.transactionDetails}</td>
                            <td>${transaction.amount}</td>
                            <td>${transaction.balance}</td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="4">No transactions available</td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </section>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Admin;
