import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import APIStored from "../API/APIStored";
import axios from "axios"; // Import axios for API requests
// import bcrypt from "bcryptjs";
import image from "../../image/login-backgroundImage.jpg";

function Login() {
  const navigate = useNavigate();

  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });

  const [error, setError] = useState({
    userName: false,
    password: false,
  });
  const [data, setData] = useState([]);
  useEffect(() => {
    // Fetch user data from the API on component mount
    APIStored()
      .then((response) => {
        console.log(response.data); // Log response data for debugging
        setData(response.data); // Set user data to state
      })
      .catch((error) => console.error("Error fetching data", error));
  }, []);

  // Handle Login Submission
  async function onLogin(event) {
    event.preventDefault();

    // Check for empty fields
    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      return;
    }

    try {
      // Send authentication request to your backend
      const response = await axios.post(
        "http://localhost:8080/api/authenticate",
        {
          username: login.userName,
          password: login.password, // Send the plain password here for JWT generation
        }
      );

      // Log the full response object to see what is being returned
      console.log("Full Response:", response);

      if (response.status === 200) {
        // Store the JWT token in localStorage
        const token = response.data.jwt;
        console.log("Received Token:", token); // Log the token for debugging
        localStorage.setItem("authToken", tokenData.id);
        console.log("authToken", token);

        // Decode token to get user ID or other details if needed
        // const userId = parseJwt(token).id; // Assuming `id` is in the token payload
        const tokenData = parseJwt(token);
        console.log("Token Data:", tokenData); // Log the token data for debugging
        // const userId = tokenData.id; // Assuming `id` is in the token payload
        const userId = tokenData.id;
        console.log("User ID:", userId); // Log the user ID for debugging
        axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;

        // Fetch account data and user details
        const accountResponse = await axios.get(
          `http://localhost:8080/api/account/userid/${userId}`
        );
        const userResponse = await axios.get(
          `http://localhost:8080/api/users/${userId}`
        );

        // Navigate to the dashboard with account data and user details
        navigate(`/dashboard/${userId}`, {
          state: {
            JWTaccount: accountResponse.data,
            SetJWTuser: userResponse.data,
          },
        });
      } else {
        setError({
          userName: true,
          password: true,
        });
        console.log("Invalid username or password");
      }
    } catch (error) {
      setError({
        userName: true,
        password: true,
      });
      console.error("Authentication failed", error);
    }
  }

  function parseJwt(token) {
    if (!token) {
      console.error("Token is undefined or null");
      return {};
    }
    try {
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const payload = JSON.parse(window.atob(base64));
      return {
        id: payload.userId,
        email: payload.sub,
      };
    } catch (error) {
      console.error("Error parsing JWT token:", error);
      return {};
    }
  }

  function onLoginChange(event) {
    const { name, value } = event.target;
    setLogin((prevLoginData) => ({
      ...prevLoginData,
      [name]: value,
    }));

    setError((prevError) => ({
      ...prevError,
      [name]: !value, // Set error state based on input value
    }));
  }

  return (
    <div className="Login-page">
      <img src={image} className="login-image" alt="login-background" />
      <form className="Login-form" onSubmit={onLogin}>
        <div className="bank-name">Easy Online Banking</div>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <input
            className={`userName ${error.userName ? "input" : ""}`}
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="Email ID"
            onChange={onLoginChange}
            value={login.userName}
            style={{ borderColor: error.userName ? "red" : "" }}
          />
          {error.userName && (
            <span style={{ color: "red" }} className="error-text">
              Please Enter User Id
            </span>
          )}
        </div>
        <div className="input-group">
          <input
            className={`password ${error.password ? "input" : ""}`}
            type="password"
            name="password"
            autoComplete="off"
            placeholder="Password"
            onChange={onLoginChange}
            value={login.password}
            style={{ borderColor: error.password ? "red" : "" }}
          />
          <span className="show-password" id="show-password-btn"></span>
          {error.password && (
            <span style={{ color: "red" }} className="error-text">
              Please Enter Password
            </span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}

export default Login;
