import React, { useEffect, useState } from "react";
import logo from "../login-page/test-logo.avif";
import "../Dashboard/dashboard.css";
// import Trasaction from "./Transaction";
import axios from "axios";
import { useParams } from "react-router-dom";

function Dashboard() {
  const { userId } = useParams(); // Get the user id from the URL
  const [account, setAccount] = useState({}); // Initialize account state for account data

  useEffect(() => {
    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`) // Now using userId in the URL
        .then((response) => {
          setAccount(response.data);
        })
        .catch((error) => console.log("Error fetching account data:", error));
    }
  }, [userId]);

  if (!account.acc_Id) {
    return <div>Loading...</div>; // Handle loading state
  }

  return (
    <div className="Dashboard">
      <div className="nav-bar">
        <img src={logo} alt="logo" className="bank-logo" />
        <div className="nav-bar-data">
          <button>Dashboard</button>
          <button>Loan</button>
          <button>Cards</button>
          <button>Fund Transfer</button>
          <button>Deposit</button>
          <button>ðŸ•­</button>
        </div>

        {/* Display account details */}
        <div className="account-Balance">
          <p>Account Balance: {account.acc_Balance}</p>
        </div>
        <div className="account-Details">
          <p>User Name: {account.user_Id}</p>
          <p>Account Number: {account.acc_Number}</p>
          <p>Account Type: {account.acc_Type}</p>
          <p>Account Created Date: {account.acc_Created_Date}</p>
        </div>

        {/* Transaction statement */}
        <div className="transaction">
          <p>Transaction History</p>
          {/* Render the transaction details */}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
