import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import APIStored from "../API/APIStored";

function Login() {
  const navigate = useNavigate();

  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });

  const [error, setError] = useState({
    userName: false,
    password: false,
  });

  const [data, setData] = useState([]); // Initialize data as an empty array

  useEffect(() => {
    APIStored()
      .then((response) => {
        // Assuming response.data is directly an array of user objects
        console.log(response.data); // Log response data for debugging
        setData(response.data); // Set data state directly if response.data is an array
      })
      .catch((error) => console.error("Error fetching data", error));
  }, []);

  function onLogin(event) {
    event.preventDefault();

    // Check for empty fields
    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      return;
    }

    // Authenticate the user
    const user = data.find(
      (user) =>
        user.email === login.userName && user.password === login.password
    );

    if (user) {
      // User authenticated successfully
      navigate("/dashboard");
      setLogin({ userName: "", password: "" });
    } else {
      // Invalid credentials
      setError({
        userName: true,
        password: true,
      });
      console.log("Invalid username or password");
    }
  }

  function onLogin(event) {
    event.preventDefault();

    // Check for empty fields
    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      return;
    }

    // Authenticate the user
    const user = data.find(
      (user) =>
        user.email === login.userName && user.password === login.password
    );

    if (user) {
      // User authenticated successfully
      // Pass the userId to the dashboard
      navigate(`/dashboard/${user.id}`); // Assuming user.id is the logged-in user's ID
      setLogin({ userName: "", password: "" });
    } else {
      // Invalid credentials
      setError({
        userName: true,
        password: true,
      });
      console.log("Invalid username or password");
    }
  }

  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onLogin}>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <input
            className={`userName ${error.userName ? "input-error" : ""}`}
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="User Name"
            onChange={onLoginChange}
            value={login.userName}
            style={{ borderColor: error.userName ? "red" : "" }}
          />
          {error.userName && (
            <span style={{ color: "red" }}>Please Enter User Name</span>
          )}
        </div>
        <div className="input-group">
          <input
            className={`password ${error.password ? "input-error" : ""}`}
            type="password"
            name="password"
            placeholder="Password"
            onChange={onLoginChange}
            value={login.password}
            style={{ borderColor: error.password ? "red" : "" }}
          />
          {error.password && (
            <span style={{ color: "red" }}>Please Enter Password</span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}

export default Login;
