import React, { useState } from "react";
import Deposit from "./Deposit"; // Ensure you have the correct import

function Dashboard({ userDetail, account }) {
  const [depositPopUp, setDepositPopUp] = useState(false);

  // Function to handle deposit button click
  const openDepositModal = () => {
    setDepositPopUp(true);
  };

  return (
    <div className={depositPopUp ? "Dashboard dashboard-blur" : "Dashboard"}>
      <div className="page-size">
        <div className="nav-bar">
          <div className="nav-bar-data">
            <button className="dashboard">Dashboard</button>
            <button className="loan">Loan</button>
            <button className="cards">Cards</button>
            <button onClick={openDepositModal} className="deposit">
              Deposit
            </button>
            <button className="fund">Fund Transfer</button>
            <button className="bell">ðŸ•­</button>
            <span className="userName">{userDetail.first_Name}</span>
            <button className="img">ðŸ‘¤</button>
          </div>
        </div>
        {depositPopUp && <Deposit setDeposit={setDepositPopUp} />}{" "}
        {/* Modal Popup */}
        <div className="acc-bal">Account Balance</div>
        <div className="account-test">
          <div className="account-Balance">
            <p className="amount">Account Balance: {account.accBalance}</p>
          </div>
          <div className="acc-det">Account Details</div>
          <div className="account">
            <div className="account-Details">
              <div className="user-detail">
                <p>User Name: {account.accId}</p>
                <p>Account Number: {account.accNumber}</p>
                <p>Account Type: {account.accType}</p>
                <p>Account Created Date: {account.accCreatedAt}</p>
              </div>
            </div>
          </div>
        </div>
        <div className="transaction">
          <p className="transaction-header">Transaction History</p>
          {/* Render the transaction details */}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
