import ReactDOM from "react-dom";
import "./deposit.css";
import React, { useContext, useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { AccountContext } from "../Context/AccountProvider";

function Deposit({ setDeposit }) {
  const [form, setFrom] = useState({
    userId: "",
    accNumber: "",
    accBalance: "",
    accCreatedAt: "",
    accType: "savings",
  });
  const portalRoot = document.getElementById("portalRoot");
  const { account, setAccount } = useContext(AccountContext);

  console.log(JSON.stringify(account));

  useEffect(() => {
    const userId = localStorage.getItem("userId");

    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setAccount(response.data);
          console.log(response.data);
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
        });
    }
    // const account = {
    //   userId: userId,
    //   accNumber: account.accNumber,
    //   accBalance: account.accBalance,
    //   accCreatedAt: account.accCreatedAt,
    //   accType: account.accType,
    // };
    axios
      .put(`http://localhost:8080/api/account/userid/${userId}`, account, {
        headers: { "Content-Type": "application/json" },
      })
      .then((response) => {
        console.log(response.data);
        console.log(account.accBalance);
      })
      .catch((error) => console.log(error));
  }, [setAccount]);

  console.log(JSON.stringify(account));
  if (!portalRoot) {
    console.error("Portal root not found");
    return null;
  }

  function handleChange(e) {
    const userId = localStorage.getItem("userId");
    const updatedAccount = { ...account, accBalance: e.target.value };
    setAccount(updatedAccount);
    axios
      .put(`http://localhost:8080/api/account/userid/${userId}`)
      .then((response) => {
        setAccount(response.data);
        console.log(response.data);
        console.log("PUT REQUEST HAS BEEN MADE ON " + userId);
      })
      .catch((error) => {
        console.log(error);
      });

    setAccount((preForm) => {
      return {
        ...preForm,
        accBalance: e.target.value,
      };
    });
  }

  const amountUpdate = async (e) => {
    e.preventDefault();
    // setAccount(e.target.value);
    setAccount((preForm) => {
      return {
        ...preForm,
        accBalance: e.target.value,
      };
    });
  };

  // on click handler
  function onClickHandler(e) {
    setDeposit(false);
    setAccount((preForm) => {
      return {
        ...preForm,
        accBalance: e.target.value,
      };
    });
  }
  // Make a POST request to deposit the amount
  console.log(account.accBalance);
  return ReactDOM.createPortal(
    <div className="deposit-overlay" onClick={onClickHandler}>
      <div className="deposit-modal" onClick={(e) => e.stopPropagation()}>
        <div className="deposit-inner">
          <h1>Deposit</h1>
          <label className="amount-details">Amount:</label>
          <input
            type="number"
            className="amount"
            name="accBalance"
            value={account.accBalance}
            placeholder="Enter Amount Here"
            required
            onChange={amountUpdate}
          />
          <button type="submit" onClick={handleChange}>
            Deposit
          </button>
          <button className="close-button" onClick={() => setDeposit(false)}>
            Close
          </button>
        </div>
      </div>
    </div>,
    portalRoot
  );
}
export default Deposit;
