import React from "react";
import "./deposit.css";
import ReactDOM from "react-dom";

function Deposit({ deposit, setDeposit }) {
  return ReactDOM.createPortal(
    deposit
      ? ((
          <div className="deposit">
            <div className="deposit-inner">
              <h1>Deposit</h1>
              <label className="amount-details">Amount:</label>
              <input type="number" className="amount" name="amount" required />
              <button type="submit">Deposit</button>
              <button className="close-button" onClick={() => setDeposit(true)}>
                Close
              </button>
            </div>
          </div>
        ),
        document.querySelector(".portal-root"))
      : ""
  );
}

export default Deposit;
