import ReactDOM from "react-dom";
import "./deposit.css";
import React, { useContext, useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { AccountContext } from "../Context/AccountProvider";

function FundTransfer({ setFundTransfer }) {
  const portalRoot = document.getElementById("portalRoot");
  const { account, setAccount } = useContext(AccountContext);
  const [inputAmount, setInputAmount] = useState(""); // Initialize with current balance or empty

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setAccount(response.data);
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
        });
    }
  }, [setAccount]);

  if (!portalRoot) {
    console.error("Portal root not found");
    return null;
  }

  // Handle input change
  function handleInputChange(e) {
    setInputAmount(e.target.value);
  }

  // Handle form submit (deposit action)
  function handleDeposit(e) {
    e.preventDefault();
    const userId = localStorage.getItem("userId");

    // Conditions on fund transfer
    if (account.accBalance < inputAmount || account.accBalance === 0) {
      alert("Insufficient balance");
      return;
    } else if (inputAmount < 0) {
      alert("Please insert a valid amount");
      return;
    }
    // // Prepare updated account object with the new balance
    const updatedAccount = {
      ...account,
      accBalance: account.accBalance - Number(inputAmount),
    };

    console.log("Updated account data before PUT:", updatedAccount); // Debug log

    // // Send PUT request to update account balance in the DB
    axios
      .put(
        `http://localhost:8080/api/account/userid/${userId}`,
        updatedAccount,
        {
          headers: { "Content-Type": "application/json" },
        }
      )
      .then((response) => {
        console.log("Response from PUT request:", response.data); // Debug log
        setAccount(response.data); // Update account in context with the response
        console.log("Account balance updated in context:", response.data);
        console.log(
          "this is your added balance" +
            (account.accBalance + Number(inputAmount))
        );
        console.log(
          `THIS IS FOR TESTIG IF THE ACCOUNT NUMBETR IS GETTING ${account.accNumber}`
        );
        setFundTransfer(false); // Close the deposit modal
      })
      .catch((error) => {
        console.log("Error updating account balance:", error);
      });

    axios
      .put(`http://localhost:8080/api/account/${account.accNumber}`)
      .then((resposne) => {
        console.log(resposne.data);
      });
  }

  return ReactDOM.createPortal(
    <div className="deposit-overlay" onClick={() => setFundTransfer(false)}>
      <div className="deposit-modal" onClick={(e) => e.stopPropagation()}>
        <div className="deposit-inner">
          <h1>Fund Transfer</h1>
          <form onSubmit={handleDeposit}>
            <label className="amount-details">Amount:</label>
            <input
              type="number"
              className="amount number-input"
              name="accBalance"
              value={inputAmount}
              placeholder="Enter Amount Here"
              autoComplete="off"
              required
              onChange={handleInputChange}
            />
            <label>Account Number</label>
            <input
              type="number"
              className="account-no number-input"
              placeholder="Enter Receiver Account Number"
              autoComplete="off"
              name="accountNo"
              required
              onChange={handleInputChange}
            />
            <nav className="deposit-btn-div">
              <button type="submit" className="deposit-btn">
                FundTransafer
              </button>
            </nav>
          </form>
          <button
            className="close-button"
            onClick={() => setFundTransfer(false)}
          >
            Close
          </button>
        </div>
      </div>
    </div>,
    portalRoot
  );
}

export default FundTransfer;
