import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css"; // Keeping the same styles

function ForgotPassword() {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const [Confirm_Password, SetConfirm_Password] = useState("");
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);

  async function onResetPassword(event) {
    event.preventDefault();

    if (!email) {
      setError(true);
      return;
    }

    setError(false);
    const userId = sessionStorage.getItem("userId");
    try {
      const response = await axios.put(
        `http://localhost:8080/api/user/${userId}`, // Replace with your API endpoint
        { email }
      );

      if (response.status === 200) {
        setSuccess(true);
        // Optionally, redirect or show a success message
        console.log("Password reset link sent!");
      } else {
        setError(true);
      }
    } catch (err) {
      console.error("Error resetting password", err);
      setError(true);
    }
  }

  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onResetPassword}>
        <h1 className="Login-text">Forgot Password</h1>
        <div className="input-group">
          <label>Email ID</label>
          <input
            type="email"
            autoComplete="off"
            placeholder="Email ID"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
          {error && (
            <span className="error-text">Please Enter Valid Email ID</span>
          )}
          <label>Password</label>
          <input
            type="email"
            autoComplete="off"
            placeholder="Password"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => setPassword(e.target.value)}
            value={Password}
          />
          {error && <span className="error-text">Please Enter Password</span>}
          <label>Confirm Password</label>
          <input
            type="email"
            autoComplete="off"
            placeholder="Confirm Password"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => SetConfirm_Password(e.target.value)}
            value={Confirm_Password}
          />
        </div>
        {success && (
          <span className="success-text">
            Password reset link sent successfully!
          </span>
        )}
        <button className="Login-btn" type="submit">
          Reset Password
        </button>
        <a href="/login" className="Forgot-password-link">
          Back to Login
        </a>
      </form>
    </div>
  );
}

export default ForgotPassword;
