import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import "./deposit.css";

function Deposit({ setDeposit }) {
  const [portalRoot, setPortalRoot] = useState(null);

  useEffect(() => {
    // Find or create the portal root element
    let element = document.getElementById("portalRoot");
    if (!element) {
      element = document.createElement("div");
      element.id = "portalRoot";
      document.body.appendChild(element);
    }
    setPortalRoot(element);

    // Cleanup function to remove the portal root if we created it
    return () => {
      if (element && element.parentNode === document.body) {
        document.body.removeChild(element);
      }
    };
  }, []);

  if (!portalRoot) {
    return null; // Don't render anything if portal root is not available yet
  }

  return ReactDOM.createPortal(
    <div className="deposit">
      <div className="deposit-inner">
        <h1>Deposit</h1>
        <label className="amount-details">Amount:</label>
        <input type="number" className="amount" name="amount" required />
        <button type="submit">Deposit</button>
        <button className="close-button" onClick={() => setDeposit(false)}>
          Close
        </button>
      </div>
    </div>,
    portalRoot
  );
}

export default Deposit;
