import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import APIStored from "../API/APIStored";
import image from "../../image/login-backgroundImage.jpg";

function Login() {
  const navigate = useNavigate();

  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });

  const [error, setError] = useState({
    userName: false,
    password: false,
  });

  const [data, setData] = useState([]); // Initialize data as an empty array

  useEffect(() => {
    // Fetch user data from the API on component mount
    APIStored()
      .then((response) => {
        console.log(response.data); // Log response data for debugging
        setData(response.data); // Set user data to state
      })
      .catch((error) => console.error("Error fetching data", error));
  }, []);

  function onLogin(event) {
    event.preventDefault();

    // Check for empty fields
    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      return;
    }

    // Authenticate the user
    const user = data.find(
      (user) =>
        user.email === login.userName && user.password === login.password
    );

    if (user) {
      // Store the userId in localStorage after successful login
      localStorage.setItem("userId", user.id);

      // Navigate to the dashboard
      navigate(`/dashboard/${user.id}`);
    } else {
      // Invalid credentials
      setError({
        userName: true,
        password: true,
      });
      console.log("Invalid username or password");
    }
  }

  function onLoginChange(event) {
    const { name, value } = event.target;
    setLogin((prevLoginData) => ({
      ...prevLoginData,
      [name]: value,
    }));

    setError((prevError) => ({
      ...prevError,
      [name]: !value, // Set error state based on input value
    }));
  }

  return (
    <div className="Login-page">
      <img src={image} className="image" />
      <form className="Login-form" onSubmit={onLogin}>
        <div className="bank-name">Easy Online Banking</div>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <input
            className={`userName ${error.userName ? "input" : ""}`}
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="Email ID"
            onChange={onLoginChange}
            value={login.userName}
            style={{ borderColor: error.userName ? "red" : "" }}
          />
          {error.userName && (
            <span style={{ color: "red" }} className="error-text">
              Please Enter User Id
            </span>
          )}
        </div>
        <div className="input-group">
          <input
            className={`password ${error.password ? "input" : ""}`}
            type="password"
            name="password"
            autoComplete="off"
            placeholder="Password"
            onChange={onLoginChange}
            value={login.password}
            style={{ borderColor: error.password ? "red" : "" }}
          />
          <span class="show-password" id="show-password-btn"></span>
          {error.password && (
            <span style={{ color: "red" }} className="error-text">
              Please Enter Password
            </span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}

export default Login;
