import React, { useEffect, useState } from "react";
import logo from "../login-page/test-logo.avif";
import "../Dashboard/dashboard.css";
import Trasaction from "./Transaction";
import axios from "axios";
import { useParams } from "react-router-dom";

function Dashboard() {
  const { id } = useParams(); // Get the account id from the URL
  const [res, setRes] = useState({}); // Initialize `res` as null for account data

  axios
    .post("http://localhost:8080/api/account", {
      headers: {
        "Content-Type": "application/json",
      },
    })
    .then((response) => {
      const userId = response.data.accId; // Get the user ID from the response

      console.log("User created with ID:", userId);
    });

  useEffect(() => {
    if (id) {
      axios
        .get(`http://localhost:8080/api/account/${id}`)
        .then((response) => {
          setRes(response.data); // Store account details
        })
        .catch((error) => console.log("Error fetching account data:", error));
    }
  }, [id]);

  if (!res.acc_Id) {
    return <div>Loading...</div>; // Handle loading state
  }

  console.log("Account ID from URL:", id);
  if (!res) {
    return <div>Loading...</div>; // Add loading state while fetching data
  }

  const data = [1, 2, 3, 4, 5]; // Dummy data for transaction history
  const transactionDetail = data.map(() => {
    return <Trasaction />;
  });

  return (
    <div className="Dashboard">
      <div className="nav-bar">
        <img src={logo} alt="logo" className="bank-logo" />
        <div className="nav-bar-data">
          <button>Dashboard</button>
          <button>Loan</button>
          <button>Cards</button>
          <button>Fund Transfer</button>
          <button>Deposit</button>
          <button>ðŸ•­</button>
        </div>

        {/* Display account details */}
        <div className="account-Balance">
          <div>{}</div>
        </div>
        <div className="account-Details">
          <p>User Name: </p>
          <p>Account Number: </p>
          <p key={1}>Account Type: {"Savings"}</p>
        </div>

        {/* Transaction statement */}
        <div className="transaction">
          <p>Transaction History</p>
          {transactionDetail}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
