import React, { useEffect, useState } from "react";
import logo from "../login-page/test-logo.avif";
import "../Dashboard/dashboard.css";
import Trasaction from "./Transaction";
import axios from "axios";
import { useParams } from "react-router-dom";

function Dashboard() {
  const { id } = useParams(); // Get the account id from the URL
  const [res, setRes] = useState(null); // Initialize `res` as null for account data

  const API = (empID) =>
    axios.get("http://localhost:8080/api/account/" + empID); // Add missing '/'

  useEffect(() => {
    if (id) {
      API(id)
        .then((response) => {
          // Store all account details in the `res` state
          setRes({
            acc_Id: response.data.acc_Id,
            acc_Number: response.data.acc_Number,
            acc_Balance: response.data.acc_Balance,
          });
        })
        .catch((error) => console.log(error));
    }
  }, [id]);

  if (!res) {
    return <div>Loading...</div>; // Add loading state while fetching data
  }

  const data = [1, 2, 3, 4, 5]; // Dummy data for transaction history
  const transactionDetail = data.map(() => {
    return <Trasaction />;
  });

  return (
    <div className="Dashboard">
      <div className="nav-bar">
        <img src={logo} alt="logo" className="bank-logo" />
        <div className="nav-bar-data">
          <button>Dashboard</button>
          <button>Loan</button>
          <button>Cards</button>
          <button>Fund Transfer</button>
          <button>Deposit</button>
          <button>ðŸ•­</button>
        </div>

        {/* Display account details */}
        <div className="account-Balance">
          <div>{res.acc_Balance}</div>
        </div>
        <div className="account-Details">
          <p>User Name: {res.acc_Id}</p>
          <p>Account Number: {res.acc_Number}</p>
          <p>Account Type: {"Savings"}</p>
        </div>

        {/* Transaction statement */}
        <div className="transaction">
          <p>Transaction History</p>
          {transactionDetail}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
