import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import "../AdminDashboard/admin.css";

const Admin = () => {
  const [userDetails, setUserDetails] = useState(null);
  const [accountNumber, setAccountNumber] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const { adminJWT } = location.state || {};
    if (adminJWT) {
      localStorage.setItem("adminToken", adminJWT);
    }

    const token = localStorage.getItem("adminToken");
    if (!token) {
      navigate("/admin-login");
    }
  }, [location, navigate]);

  const getAuthHeader = () => {
    const token = localStorage.getItem("adminToken");
    return { Authorization: `Bearer ${token}` };
  };

  const fetchUserDetails = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/api/account/${accountNumber}`,
        {
          headers: getAuthHeader(),
        }
      );
      setUserDetails(response.data);
      setError("");
    } catch (error) {
      console.error("Error fetching user details:", error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError("Failed to fetch user details. Please try again.");
      }
      setUserDetails(null);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async () => {
    try {
      await axios.delete(
        `http://localhost:8080/api/admin/user/${userDetails.userId}`,
        {
          headers: getAuthHeader(),
        }
      );
      alert("User deleted successfully");
      setUserDetails(null);
      setAccountNumber("");
    } catch (error) {
      console.error("Error deleting user:", error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError("Failed to delete user. Please try again.");
      }
    }
  };

  const handleLoanAction = async (action) => {
    try {
      await axios.post(
        `http://localhost:8080/api/admin/loan/${userDetails.loanId}`,
        { action },
        { headers: getAuthHeader() }
      );
      fetchUserDetails();
    } catch (error) {
      console.error(`Error ${action} loan:`, error);
      if (error.response && error.response.status === 403) {
        setError("Access denied. Please log in again.");
        handleLogout();
      } else {
        setError(`Failed to ${action} loan. Please try again.`);
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    navigate("/admin-login");
  };

  return (
    <div className="admin-dashboard">
      <div className="admin-dashboard-container">
        <header className="admin-dashboard-header">
          <span className="admin-dashboard-logo">Easy Banking</span>
          <h1 className="admin-dashboard-title">Admin Dashboard</h1>
          <div className="admin-user-info">
            <span className="admin-username">Admin</span>
            <span className="admin-user-avatar">👤</span>
          </div>
        </header>

        <div className="admin-dashboard-content">
          <nav className="admin-dashboard-nav">
            <button onClick={() => navigate("/admin-dashboard")}>
              Dashboard
            </button>
            <button onClick={() => navigate("/admin-users")}>
              Manage Users
            </button>
            <button onClick={() => navigate("/admin-loans")}>
              Manage Loans
            </button>
            <button onClick={() => navigate("/admin-reports")}>Reports</button>
            <button onClick={handleLogout}>Log out</button>
          </nav>

          <main className="admin-dashboard-main">
            <section className="admin-user-search">
              <h2>User Search</h2>
              <div className="admin-search-input">
                <input
                  type="text"
                  value={accountNumber}
                  onChange={(e) => setAccountNumber(e.target.value)}
                  placeholder="Enter Account Number"
                />
                <button onClick={fetchUserDetails} disabled={loading}>
                  {loading ? "Searching..." : "Search"}
                </button>
              </div>
              {error && <p className="error-message">{error}</p>}
            </section>

            {userDetails && (
              <section className="admin-user-details">
                <h2>User Details</h2>
                <div className="user-info">
                  <p>
                    <strong>Name:</strong> {userDetails.firstName}{" "}
                    {userDetails.lastName}
                  </p>
                  <p>
                    <strong>Account Number:</strong> {userDetails.accountNumber}
                  </p>
                  <p>
                    <strong>Account Balance:</strong> $
                    {userDetails.accountBalance}
                  </p>
                  <p>
                    <strong>Email:</strong> {userDetails.email}
                  </p>
                </div>

                <div className="admin-actions">
                  <button
                    onClick={handleDeleteUser}
                    className="delete-user-btn"
                  >
                    Delete User
                  </button>
                </div>

                {userDetails.loanDetails && (
                  <div className="loan-details">
                    <h3>Loan Details</h3>
                    <p>
                      <strong>Loan Amount:</strong> $
                      {userDetails.loanDetails.amount}
                    </p>
                    <p>
                      <strong>Loan Status:</strong>{" "}
                      {userDetails.loanDetails.status}
                    </p>
                    {userDetails.loanDetails.status === "PENDING" && (
                      <div className="loan-actions">
                        <button
                          onClick={() => handleLoanAction("approve")}
                          disabled={
                            userDetails.loanDetails.status === "REJECTED"
                          }
                        >
                          Approve Loan
                        </button>
                        <button
                          onClick={() => handleLoanAction("reject")}
                          className={
                            userDetails.loanDetails.status === "REJECTED"
                              ? "rejected"
                              : ""
                          }
                        >
                          Reject Loan
                        </button>
                      </div>
                    )}
                  </div>
                )}

                <div className="transaction-history">
                  <h3>Transaction History</h3>
                  <table>
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Description</th>
                        <th>Amount</th>
                        <th>Balance</th>
                      </tr>
                    </thead>
                    <tbody>
                      {userDetails.transactions.map((transaction, index) => (
                        <tr key={index}>
                          <td>{transaction.date}</td>
                          <td>{transaction.description}</td>
                          <td>${transaction.amount}</td>
                          <td>${transaction.balance}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </section>
            )}
          </main>
        </div>
      </div>
    </div>
  );
};

export default Admin;
