import ReactDOM from "react-dom";
import "../Dashboard/loan.css";
import React, { useContext, useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { AccountContext } from "../Context/AccountProvider";

function Loan({ setLoan }) {
  const portalRoot = document.getElementById("portalRoot");
  const { account } = useContext(AccountContext);
  const [loanType, setLoanType] = useState("Home Loan"); // Default loan type
  const [loanData, setLoanData] = useState({
    userId: localStorage.getItem("userId"),
    userName: "",
    userAddress: "",
    loanType: "Home Loan",
    amountRequired: 0,
    accountNumber: account ? account.accNumber : "",
    mobileNumber: "",
    loanStatus: "Approved",
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setLoanData((prev) => ({
            ...prev,
            accountNumber: response.data.accNumber, // Update account number from account API
          }));
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
        });
    }
  }, []);

  if (!portalRoot) {
    console.error("Portal root not found");
    return null;
  }

  // Handle input changes for loan data
  function handleInputChange(e) {
    const { name, value } = e.target;
    setLoanData((prev) => ({
      ...prev,
      [name]: value,
    }));
  }

  // Handle loan type change
  function handleLoanTypeChange(e) {
    setLoanType(e.target.value);
    setLoanData((prev) => ({
      ...prev,
      loanType: e.target.value,
    }));
  }

  // Handle loan form submission
  function handleLoanSubmission(e) {
    e.preventDefault();
    const userId = localStorage.getItem("userId");

    const newLoan = {
      ...loanData,
      userId: userId,
      loanStatus: "Approved", // Default status for now
    };

    console.log("Updated loan data before POST:", newLoan); // Debug log

    // Post request to create a new loan record
    axios
      .post("http://localhost:8080/api/loan", newLoan, {
        headers: { "Content-Type": "application/json" },
      })
      .then((res) => {
        console.log("Loan successfully recorded:", res.data);
        setLoan(false); // Close the loan modal
        console.log("Loan Details are Being Fetched");
      })
      .catch((error) => console.log("Error recording loan:", error));
  }

  return ReactDOM.createPortal(
    <div className="loan-overlay" onClick={() => setLoan(false)}>
      <div className="loan-modal" onClick={(e) => e.stopPropagation()}>
        <div className="loan-inner">
          <h1>Apply for a Loan</h1>
          <form onSubmit={handleLoanSubmission}>
            {/* User Name */}
            <label className="input-label">Full Name:</label>
            <input
              type="text"
              name="userName"
              className="input-field"
              value={loanData.userName}
              placeholder="Enter your full name"
              required
              onChange={handleInputChange}
            />

            {/* User Address */}
            <label className="input-label">Address:</label>
            <input
              type="text"
              name="userAddress"
              className="input-field"
              value={loanData.userAddress}
              placeholder="Enter your address"
              required
              onChange={handleInputChange}
            />

            {/* Mobile Number */}
            <label className="input-label">Mobile Number:</label>
            <input
              type="text"
              name="mobileNumber"
              className="input-field"
              value={loanData.mobileNumber}
              placeholder="Enter your mobile number"
              required
              onChange={handleInputChange}
            />

            {/* Loan Type Dropdown */}
            <label className="input-label">Loan Type:</label>
            <select
              name="loanType"
              value={loanType}
              onChange={handleLoanTypeChange}
              required
            >
              <option value="Home Loan">Home Loan</option>
              <option value="Gold Loan">Gold Loan</option>
              <option value="Personal Loan">Personal Loan</option>
              <option value="Business Loan">Business Loan</option>
              <option value="Vehicle Loan">Vehicle Loan</option>
            </select>

            {/* Loan Amount */}
            <label className="input-label">Amount Required:</label>
            <input
              type="number"
              name="amountRequired"
              className="input-field"
              value={loanData.amountRequired}
              placeholder="Enter Loan Amount"
              required
              onChange={handleInputChange}
            />
            <div className="loan-submit-btn">
              <button type="submit" className="loan-btn">
                Apply for Loan
              </button>
            </div>
          </form>
          <button className="close-button" onClick={() => setLoan(false)}>
            Close
          </button>
        </div>
      </div>
    </div>,
    portalRoot
  );
}

export default Loan;
