import ReactDOM from "react-dom";
import "./deposit.css";
import React, { useEffect, useState, useContext } from "react";
function Deposit({ setDeposit }) {
  const portalRoot = document.getElementById("portalRoot");
  const { account, setAccount } = useContext(AccountContext);

  if (!portalRoot) {
    console.error("Portal root not found");
    return null;
  }

  return ReactDOM.createPortal(
    <div className="deposit-overlay" onClick={() => setDeposit(false)}>
      <div className="deposit-modal" onClick={(e) => e.stopPropagation()}>
        <div className="deposit-inner">
          <h1>Deposit</h1>
          <label className="amount-details">Amount:</label>
          <input
            type="number"
            className="amount"
            name="amount"
            placeholder="Enter Amount Here"
            required
          />
          <button
            type="submit"
            onClick={async (e) => {
              e.preventDefault();
              // Get the input value
              const amount = e.target.form.amount.value;

              // Make a POST request to deposit the amount
              const response = await fetch(
                "http://localhost:8080/api/account/deposit",
                {
                  method: "POST",
                  headers: {
                    "Content-Type": "application/json",
                  },
                  body: JSON.stringify({
                    userId: account.userId,
                    amount,
                  }),
                }
              );

              // If the deposit is successful, update the account balance
              if (response.ok) {
                const updatedAccount = await response.json();
                setAccount(updatedAccount);
              }
            }}
          >
            Deposit
          </button>
          <button className="close-button" onClick={() => setDeposit(false)}>
            Close
          </button>
        </div>
      </div>
    </div>,
    portalRoot
  );
}

export default Deposit;
