import React from "react";
import "../Register/register.css";
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

function Register() {
  const accountApi = "http://localhost:8080/api/account";
  const navigate = useNavigate();

  const [form, setForm] = useState({
    first_Name: "",
    last_Name: "",
    mobile: "",
    email: "",
    password: "",
    confirmPass: "",
    terms: false,
  });

  const [errors, setErrors] = useState({
    first_Name: false,
    mobile: false,
    email: false,
    password: false,
    confirmPass: false,
    passwordMismatch: false,
    terms: false,
  });

  function onSubmit(event) {
    event.preventDefault();
    const newErrors = {
      first_Name: !form.first_Name,
      mobile: !form.mobile,
      email: !form.email,
      password: !form.password,
      confirmPass: !form.confirmPass,
      terms: !form.terms,
      passwordMismatch: form.password !== form.confirmPass,
    };

    setErrors(newErrors);

    if (Object.values(newErrors).some((error) => error)) {
      console.log("Please fill all the details correctly.");
      return;
    }

    // Proceed if all fields are filled and passwords match
    console.log("Registration successful:", form);
    // navigate("/dashboard");

    setForm({
      first_Name: "",
      last_Name: "",
      mobile: "",
      email: "",
      password: "",
      confirmPass: "",
      terms: false,
    });
    axios
      .post("http://localhost:8080/api/users", form, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((response) => {
        const userID = response.data.userID;
        console.log(userID);
        console.log(response.data);
      })
      .catch((errors) => console.log(errors));

    // setting default values to user
    const account = {
      accId: "",
      accNumber: Math.random() * 99999999,
      accBalance: 0,
      accCreatedAt: new Date(),
      accType: "",
    };
    axios
      .post(accountApi, account, {
        headers: {
          "Content-Type": "applicatoin/json",
        },
      })
      .then((response) => {
        // setAccountDate(response.data);
        console.log(response.data);
      })
      .catch((error) => console.log(error));
  }

  function handleCreateAccount(event) {
    const { name, value, type, checked } = event.target;
    setForm((preFormData) => {
      return {
        ...preFormData,
        [name]: type === "checkbox" ? checked : value,
      };
    });

    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: false,
      passwordMismatch:
        name === "password" || name === "confirmPass"
          ? false
          : prevErrors.passwordMismatch,
    }));
  }

  return (
    <div className="Register-page">
      <form className="Register-form">
        <div className="name-row">
          <div className="input-group">
            <label>First Name</label>
            <input
              className={`firstName ${errors.first_Name ? "input-error" : ""}`}
              placeholder="First Name"
              type="text"
              name="first_Name"
              autoComplete="off"
              value={form.first_Name}
              onChange={handleCreateAccount}
              style={{ borderColor: errors.first_Name ? "red" : "" }}
            />
            {errors.first_Name && (
              <span style={{ color: "red" }}>Please Enter Your Name</span>
            )}
          </div>
          <div className="input-group">
            <label>Last Name</label>
            <input
              className="last_Name"
              placeholder="Last Name"
              type="text"
              name="last_Name"
              autoComplete="off"
              value={form.last_Name}
              onChange={handleCreateAccount}
              style={{ borderColor: errors.last_Name ? "red" : "" }}
            />
          </div>
        </div>
        <div className="name-row">
          <div className="input-group">
            <label>Email</label>
            <input
              className={`email ${errors.email ? "input-error" : ""}`}
              placeholder="Email"
              type="email"
              name="email"
              autoComplete="off"
              value={form.email}
              onChange={handleCreateAccount}
              style={{ borderColor: errors.email ? "red" : "" }}
            />
            {errors.email && (
              <span style={{ color: "red" }}>Please Enter Email</span>
            )}
          </div>
          <div className="input-group">
            <label>Mobile</label>
            <input
              className={`mobile ${errors.mobile ? "input-error" : ""}`}
              placeholder="Mobile"
              type="tel"
              pattern="[0-9]{10}"
              autoComplete="off"
              name="mobile"
              value={form.mobile}
              onChange={handleCreateAccount}
              style={{ borderColor: errors.mobile ? "red" : "" }}
            />
            {errors.mobile && (
              <span style={{ color: "red" }}>Please Enter Mobile Number </span>
            )}
          </div>
        </div>
        <div className="name-row">
          <div className="input-group">
            <label>Password</label>
            <input
              className={`password ${errors.password ? "input-error" : ""}`}
              placeholder="Password"
              type="password"
              name="password"
              value={form.password}
              onChange={handleCreateAccount}
              style={{ borderColor: errors.password ? "red" : "" }}
            />
            {errors.password && (
              <span style={{ color: "red" }}>Please Enter The Password</span>
            )}
          </div>
          <div className="input-group">
            <label>Confirm Password</label>
            <input
              className={`confirm-pass ${
                errors.confirmPass || errors.passwordMismatch
                  ? "input-error"
                  : ""
              }`}
              placeholder="Confirm Password"
              type="password"
              name="confirmPass"
              value={form.confirmPass}
              onChange={handleCreateAccount}
              style={{
                borderColor:
                  errors.confirmPass || errors.passwordMismatch ? "red" : "",
              }}
            />
            {errors.passwordMismatch && (
              <span style={{ color: "red" }}>Passwords do not match</span>
            )}
          </div>
        </div>
        <div className="terms-row">
          <input
            type="checkbox"
            id="terms"
            className={`input-checkbox ${errors.password ? "input-error" : ""}`}
            name="terms"
            checked={form.terms}
            onChange={handleCreateAccount}
          />
          <span className="terms-label">
            <Link>Creating an account T&C</Link>
          </span>
        </div>
        {errors.terms && (
          <div style={{ color: "red" }}>Please Accept Terms & Conditions</div>
        )}
        <button className="Create-acc-btn" onClick={onSubmit}>
          Sign Up
        </button>
        <div className="reg-btn">
          <button
            className="register-login-btn"
            onClick={() => navigate("/login")}
          >
            Already have an Account â†’
          </button>
        </div>
      </form>
    </div>
  );
}

export default Register;
