import ReactDOM from "react-dom";
import "./deposit.css";
import React, { useContext, useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { AccountContext } from "../Context/AccountProvider";

function Deposit({ setDeposit }) {
  const portalRoot = document.getElementById("portalRoot");
  const { account, setAccount } = useContext(AccountContext);
  const [inputAmount, setInputAmount] = useState("");
  const [transaction, setTransaction] = useState({
    TransactionID: null,
    userId: localStorage.getItem("userId"),
    transferDate: "",
    transferDetails: "",
    amountTransfered: 0,
    balance: account ? account.accBalance : 0,
  });

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setAccount(response.data);
          // Update balance in transaction state with account balance
          setTransaction((prev) => ({
            ...prev,
            balance: response.data.accBalance,
          }));
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
        });
    }
  }, [setAccount]);

  if (!portalRoot) {
    console.error("Portal root not found");
    return null;
  }

  // Handle input change for deposit amount
  function handleInputChange(e) {
    setInputAmount(e.target.value);
  }

  // Handle deposit form submission
  function handleDeposit(e) {
    e.preventDefault();
    const userId = localStorage.getItem("userId");

    // Capture the current date
    const currentDate = new Date().toISOString().split("T")[0];

    // Prepare updated account object with the new balance
    const updatedAccount = {
      ...account,
      accBalance: account.accBalance + Number(inputAmount),
    };

    console.log("Updated account data before PUT:", updatedAccount); // Debug log

    // Send PUT request to update account balance in the DB
    axios
      .put(`http://localhost:8080/api/account/user/${userId}`, updatedAccount, {
        headers: { "Content-Type": "application/json" },
      })
      .then((response) => {
        console.log("Response from PUT request:", response.data); // Debug log
        setAccount(response.data); // Update account in context with the response
        setDeposit(false); // Close the deposit modal
      })
      .catch((error) => {
        console.log("Error updating account balance:", error);
      });

    // Prepare transaction data with details of the deposit
    const updatedTransaction = {
      TransactionID: null, // Let backend generate the ID
      userId: userId,
      transferDate: currentDate,
      transferDetails: `Credited to account ${account.accNumber}`,
      amountTransfered: Number(inputAmount),
      balance: updatedAccount.accBalance,
    };

    // Post request to create a new transaction record
    axios
      .post("http://localhost:8080/api/transaction", updatedTransaction, {
        headers: { "Content-Type": "application/json" },
      })
      .then((res) => {
        console.log("Transaction successfully recorded:", res.data);
      })
      .catch((error) => console.log("Error recording transaction:", error));
  }

  return ReactDOM.createPortal(
    <div className="deposit-overlay" onClick={() => setDeposit(false)}>
      <div className="deposit-modal" onClick={(e) => e.stopPropagation()}>
        <div className="deposit-inner">
          <h1>Deposit</h1>
          <form onSubmit={handleDeposit}>
            <label className="amount-details">Amount:</label>
            <input
              type="number"
              className="amount"
              name="accBalance"
              value={inputAmount}
              placeholder="Enter Amount Here"
              autoComplete="off"
              required
              onChange={handleInputChange}
            />
            <nav>
              <button type="submit" className="deposit-btn">
                Deposit
              </button>
            </nav>
          </form>
          <button className="close-button" onClick={() => setDeposit(false)}>
            Close
          </button>
        </div>
      </div>
    </div>,
    portalRoot
  );
}

export default Deposit;
