import React, { useEffect, useState } from "react";
import logo from "../login-page/test-logo.avif";
import "../Dashboard/dashboard.css";
import Trasaction from "./Transaction";
import axios from "axios";
import { useParams } from "react-router-dom";

function Dashboard() {
  const { id } = useParams();
  const [res, setRes] = useState([]);

  const API = (empID) => axios.get("http://localhost:8080/api/account" + empID);

  const dataPop = useEffect(() => {
    if (id) {
      API(id)
        .then((response) => {
          setRes(response.data[id].acc_Id);
          setRes(response.data[id].acc_Number);
          setRes(response.data[id].acc_Balance);
        })
        .catch((error) => console.log(error));
    }
  }, [id]);

  console.log(JSON.stringify(res));
  const data = [1, 2, 3, 4, 5];
  const transactionDetail = data.map(() => {
    return <Trasaction />;
  });
  return (
    <div className="Dashboard">
      <div className="nav-bar">
        <img src={logo} alt="logo" className="bank-logo" />
        <div className="nav-bar-data">
          <button>Dashboard</button>
          <button>Loan</button>
          <button>Cards</button>
          <button>Fund Transfer</button>
          <button>Deposit</button>
          <button>ðŸ•­</button>
        </div>
        {res.map((item) => (
          <>
            <div className="account-Balance">
              <div> {dataPop} </div>
            </div>
            <div className="account-Details">
              <p>User Name : {`${dataPop}`}</p>
              <p>Account Number : {`${dataPop}`}</p>
              <p>Account Type : {`${"Savings"}`}</p>
            </div>
          </>
        ))}
        ;{/* Trasaction statement */}
        <div className="transaction">
          <p>Transaction History</p>
          {transactionDetail}
        </div>
      </div>
    </div>
  );
}
export default Dashboard;
