import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import APIStored from "../API/APIStored";

function Login() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);
  console.log(JSON.stringify([data[1].id]));

  useEffect(() => {
    fetch(APIStored)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });

  const [error, setError] = useState({
    userName: false,
    password: false,
  });

  function onLogin(event) {
    event.preventDefault();
    // true if username or password field is empty
    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    // const newLogin = {
    //   userName: !data.userName,
    //   password: !data.password,
    // };

    console.log(data.password, data.first_Name, userName, password);
    setError(newError);
    // Checking if any of the field is empty
    if (Object.values(newError).some((error) => error)) {
      // <p style={(color = "red")}>Please fill all the details</p>;
      console.log("Madetory details are not filled");

      return;
    }
    navigate("/dashboard");

    setLogin({
      userName: "",
      password: "",
    });
  }

  function onLoginChange(event) {
    const { name, value } = event.target;
    setLogin((prevLoginData) => ({
      ...prevLoginData,
      [name]: value,
    }));

    setError((prevError) => ({
      ...prevError,
      [name]: !value,
    }));
  }

  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onLogin}>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <input
            className={`userName ${error.userName ? "input-error" : ""}`}
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="User Name"
            onChange={onLoginChange}
            value={login.userName}
            style={{ borderColor: error.userName ? "red" : "" }}
          />
          {error.userName && (
            <span style={{ color: "red" }}>Please Enter User Name</span>
          )}
        </div>
        <div className="input-group">
          <input
            className={`password ${error.password ? "input-error" : ""}`}
            type="password"
            name="password"
            placeholder="Password"
            onChange={onLoginChange}
            value={login.password}
            style={{ borderColor: error.password ? "red" : "" }}
          />
          {error.password && (
            <span style={{ color: "red" }}>Please Enter Password</span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}

export default Login;
