import React, { useEffect, useState } from "react";
import "../Dashboard/dashboard.css";
import axios from "axios";
import Deposit from "./Deposit";

function Dashboard() {
  const [account, setAccount] = useState(null); // Initialize account state as null
  const [loading, setLoading] = useState(true); // Add a loading state
  const [userDetail, setUserDetils] = useState([]);
  let [depositPopUp, setDepositPopUp] = useState(false);

  useEffect(() => {
    // Retrieve stored userId from localStorage
    const userId = localStorage.getItem("userId");

    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`) // Using userId in the API call
        .then((response) => {
          console.log(response.data); // Log response data for debugging
          setAccount(response.data); // Set the retrieved account data
          setLoading(false); // Data has been fetched, stop loading
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
          setLoading(false); // Stop loading even if there's an error
        });
    }
  }, []);

  useEffect(() => {
    const userId = localStorage.getItem("userId");
    axios
      .get("http://localhost:8080/api/users/" + userId)
      .then((response) => {
        setUserDetils(response.data);
        console.log(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Handle loading state
  }

  if (!account || Object.keys(account).length === 0) {
    return <div>No account data available.</div>; // Handle case when no account data is returned
  }

  //get user name

  return (
    <div className="Dashboard">
      <div className="page-size">
        <div className="nav-bar">
          {/* <img src={logo} alt="logo" className="bank-logo" /> */}
          <div className="nav-bar-data">
            <button className="dashboard"> Dashboard</button>
            <button className="loan">Loan</button>
            <button className="cards">Cards</button>
            <button
              onClick={() => {
                setDepositPopUp(true);
              }}
              className="deposit"
            >
              Deposit
            </button>
            <button className="fund">Fund Transfer</button>

            <button className="bell">ðŸ•­</button>
            <span className="userName"> {userDetail.first_Name}</span>
            <button className="img">
              <img></img>ðŸ‘¤
            </button>
          </div>

          {/* Display account details */}
          <div className="acc-bal">Account Balance</div>
          <div className="account-test">
            <div className="account-Balance">
              <p className="amount">Account Balance: {account.accBalance}</p>
            </div>
            <div className="acc-det">Account Details</div>
            <div className="account">
              <div className="account-Details">
                <div className="user-detail">
                  <p>User Name: {account.accId}</p>
                  <p>Account Number: {account.accNumber}</p>
                  <p>Account Type: {account.accType}</p>
                  <p>Account Created Date: {account.accCreatedAt}</p>
                </div>
              </div>
            </div>
          </div>
          <div className="transaction">
            <p className="transaction-header">Transaction History</p>
            {/* Render the transaction details */}
          </div>
        </div>
      </div>
      <div className="deposit-popUp">
        <Deposit trigger={depositPopUp} setTrigger={setDepositPopUp}>
          <label className="amount-details">Amount:</label>
          <input type="number" className="amount" name="amount" required />
          <button type="submit">Deposit</button>
        </Deposit>
      </div>
    </div>
  );
}

export default Dashboard;
