function onLogin(event) {
  event.preventDefault();

  // Check for empty fields
  const newError = {
    userName: !login.userName,
    password: !login.password,
  };

  setError(newError);

  if (Object.values(newError).some((fieldError) => fieldError)) {
    console.log("Mandatory details are not filled");
    return;
  }

  // Find the user with the entered username
  const user = data.find((user) => user.email === login.userName);

  if (user) {
    // Check if the password matches
    if (user.password.toString() === login.password) {
      // User authenticated successfully
      navigate("/dashboard");
      setLogin({ userName: "", password: "" });
      setError({ userName: false, password: false }); // Clear any previous errors
    } else {
      // Password incorrect
      setError({
        userName: false,
        password: true,
      });
      console.log("Invalid password");
    }
  } else {
    // Username not found
    setError({
      userName: true,
      password: false,
    });
    console.log("Username not found");
  }
  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onLogin}>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <input
            className={`userName ${error.userName ? "input-error" : ""}`}
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="User Name"
            onChange={onLoginChange}
            value={login.userName}
            style={{ borderColor: error.userName ? "red" : "" }}
          />
          {error.userName && (
            <span style={{ color: "red" }}>
              {error.password ? "Username not found" : "Please Enter User Name"}
            </span>
          )}
        </div>
        <div className="input-group">
          <input
            className={`password ${error.password ? "input-error" : ""}`}
            type="password"
            name="password"
            placeholder="Password"
            onChange={onLoginChange}
            value={login.password}
            style={{ borderColor: error.password ? "red" : "" }}
          />
          {error.password && (
            <span style={{ color: "red" }}>
              {error.userName ? "" : "Invalid password"}
            </span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}
