import React, { useEffect, useState } from "react";
import "../Dashboard/dashboard.css";
import axios from "axios";
import Deposit from "./Deposit";
import FundTransfer from "./FundTransfer";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "../Context/AccountProvider";
import { useContext } from "react";
import "../Dashboard/Transaction/transaction.css";
import Loan from "./Loan";
import { useLocation } from "react-router-dom";

function Dashboard() {
  const { account, setAccount } = useContext(AccountContext);
  const [loading, setLoading] = useState(true);
  const [userDetail, setUserDetails] = useState({});
  const [depositPopUp, setDepositPopUp] = useState(false);
  const [fundTransferPopUp, setFundTransferPopUp] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [loanPopUp, setLoanPopUp] = useState(false);
  const location = useLocation();
  const { JWTaccount, SetJWTuser } = location.state;

  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem("userId");

    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setAccount(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
          setLoading(false);
        });

      axios
        .get("http://localhost:8080/api/users/" + userId)
        .then((response) => {
          setUserDetails(response.data);
        })
        .catch((error) => console.log(error));

      axios
        .get(`http://localhost:8080/api/transaction/${userId}`)
        .then((response) => {
          setTransactions(response.data);
          console.log(response.data);

          console.log("Transaction");
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [setAccount]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!account || Object.keys(account).length === 0) {
    return <div>No account data available.</div>;
  }

  const userId = localStorage.getItem("userId");

  return (
    <div
      className={
        depositPopUp || fundTransferPopUp
          ? "Dashboard dashboard-blur"
          : "Dashboard"
      }
    >
      <div className="dashboard-page-border">
        <div className="page-size">
          <div className="dashboard-hr"></div>
          <div className="nav-bar">
            <div className="dashbord-un-bn">
              <span className="dashboard-logo">logo</span>
              <span className="dashbord-bank-name">Easy Online Banking</span>
              <span className="dashbord-userName">{userDetail.first_Name}</span>
              <button className="dashbord-user-img">👤</button>
            </div>
            <div className="dashboard-hr"></div>
            <div className="navbar-page">
              <div className="nav-bar-data">
                <button
                  className="dashboard sidebtn"
                  onClick={() => navigate(`/dashboard/${userId}`)}
                >
                  Dashboard
                </button>
                <button
                  className="loan sidebtn"
                  onClick={() => setLoanPopUp(true)}
                  onMouseOut={() => console.log("mouse working")}
                >
                  Loan
                </button>
                <button
                  className="cards sidebtn"
                  onClick={() => navigate("/card")}
                >
                  Cards
                </button>
                <button
                  onClick={() => setDepositPopUp(true)}
                  className="deposit sidebtn"
                >
                  Deposit
                </button>
                <button
                  className="fund sidebtn"
                  onClick={() => setFundTransferPopUp(true)}
                >
                  {" "}
                  Fund Transfer
                </button>
                <button className="bell sidebtn">Log out</button>
              </div>
            </div>
            {/* Rest of your dashboard content */}
            {/* <div className="acc-bal"></div> */}
            <div className="account-test">
              <div className="dashbord-account-Balance">
                <p className="amount">
                  Account Balance: {JWTaccount.accBalance}
                </p>
              </div>
              <div className="account">
                <div className="dashboard-account-Details">
                  <div className="user-detail">
                    {/* <p>
                    User Name:{" "}
                    {userDetail.first_Name + " " + userDetail.last_Name}
                  </p> */}
                    <p>Account Number: {JWTaccount.accNumber}</p>
                    {/* <p>Account Type: {account.accType}</p> */}
                    <p>Account Created Date: {JWTaccount.accCreatedAt}</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="dashboard-trans-border">
              <p className="transaction-header" key={JWTaccount.accNumber}>
                Transaction History
              </p>
              {/* <div className="dashboard-hr"></div>   */}
              <div className="transaction">
                <table className="transaction-table table">
                  <thead className="tr-position">
                    <tr className="tr-position">
                      <th className="transaction-table sl-no ">Sl No.</th>
                      <th className="transaction-table details">
                        Transaction Details
                      </th>
                      <th className="transaction-table trans-amount">Amount</th>
                      <th className="transaction-table balance">Balance</th>
                      <th className="transaction-table date">Date</th>
                    </tr>
                  </thead>
                  {transactions.length > 0 ? (
                    transactions.map((transaction, index) => (
                      <tbody className="table-body">
                        <tr key={transaction.id} className="transaction-head">
                          <td className="index">{index + 1}</td>
                          <td className="transaction-details">
                            {transaction.transferDetails}
                          </td>
                          <td className="amount-transfered">
                            {transaction.amountTransfered}{" "}
                            <span className="credited">cr</span>
                          </td>
                          <td className="acc-balance">{transaction.balance}</td>
                          <td className="date">{transaction.transferDate}</td>
                        </tr>
                      </tbody>
                    ))
                  ) : (
                    <div className="no-transaction">
                      {" "}
                      No Transaction Avaialbel
                    </div>
                  )}
                </table>
              </div>
            </div>
          </div>
          {depositPopUp && <Deposit setDeposit={setDepositPopUp} />}
          {fundTransferPopUp && (
            <FundTransfer setFundTransfer={setFundTransferPopUp} />
          )}
          {loanPopUp && <Loan setLoan={setLoanPopUp} />}

          <div className="dashboard-hr"></div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
