import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import APIStored from "../API/APIStored";
import axios from "axios";
import image from "../../image/login-backgroundImage.jpg";

function Login() {
  const navigate = useNavigate();

  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });

  const [error, setError] = useState({
    userName: false,
    password: false,
  });

  const [data, setData] = useState([]);

  useEffect(() => {
    APIStored()
      .then((response) => {
        console.log(response.data);
        setData(response.data);
      })
      .catch((error) => console.error("Error fetching data", error));
  }, []);

  async function onLogin(event) {
    event.preventDefault();

    const newError = {
      userName: !login.userName,
      password: !login.password,
    };

    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/api/authenticate",
        {
          username: login.userName,
          password: login.password,
        }
      );

      console.log("Full Response:", response);

      if (response.status === 200) {
        const token = response.data.jwt;
        console.log("Received Token:", token);

        // Parse the JWT to extract user ID
        const tokenData = await parseJwt(token);
        console.log("Token Data:", tokenData);

        // Store JWT token and user ID in localStorage
        localStorage.setItem("authToken", token); // Store the full token
        const userId = tokenData.id;
        // setTimeout(() => {
        //   const storedUserId = localStorage.getItem("userId");
        //   console.log("Stored User ID:", storedUserId);
        // }, 1000); // Store the parsed user ID

        axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
        localStorage.setItem("userId", userId);
        const storedUserId = localStorage.getItem("userId");
        console.log("Stored User ID:", storedUserId);
        sessionStorage.setItem("userId", userId);

        // const userId = localStorage.getItem("userId");
        if (!userId) {
          console.error("Error: userId is null or undefined");
          return;
        }

        const accountResponse = await axios.get(
          `http://localhost:8080/api/account/userid/${userId}`
        );
        const userResponse = await axios.get(
          `http://localhost:8080/api/users/${userId}`
        );

        navigate(`/dashboard/${userId}`, {
          state: {
            JWTaccount: accountResponse.data,
            SetJWTuser: userResponse.data,
          },
        });
      } else {
        setError({
          userName: true,
          password: true,
        });
        console.log("Invalid username or password");
      }
    } catch (error) {
      setError({
        userName: true,
        password: true,
      });
      console.error("Authentication failed", error);
    }
  }

  function parseJwt(token) {
    if (!token) {
      console.error("Token is undefined or null");
      return {};
    }
    try {
      const base64Url = token.split(".")[1];
      const base64 = base64Url.replace(/-/g, "+").replace(/_/g, "/");
      const payload = JSON.parse(window.atob(base64));
      return {
        id: payload.userId,
        email: payload.sub,
      };
    } catch (error) {
      console.error("Error parsing JWT token:", error);
      return {};
    }
  }

  function onLoginChange(event) {
    const { name, value } = event.target;
    setLogin((prevLoginData) => ({
      ...prevLoginData,
      [name]: value,
    }));

    setError((prevError) => ({
      ...prevError,
      [name]: !value,
    }));
  }

  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onLogin}>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <label>Email ID</label>
          <input
            type="text"
            name="userName"
            autoComplete="off"
            className={`login-userName ${error.userName ? "input-error" : ""}`}
            onChange={onLoginChange}
            value={login.userName}
          />
          {error.userName && (
            <span className="error-text">Please Enter User ID</span>
          )}
        </div>
        <div className="input-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            autoComplete="off"
            className={`login-password ${error.password ? "input-error" : ""}`}
            onChange={onLoginChange}
            value={login.password}
          />
          {error.password && (
            <span className="error-text">Please Enter Password</span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <a href="/forgot-password" className="Forgot-password-link">
          Forgot Password?
        </a>
      </form>
    </div>
  );
}

export default Login;
