import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css";
import axios from "axios";

function Login() {
  console.log("Component rendering");

  const navigate = useNavigate();
  const [login, setLogin] = useState({
    userName: "",
    password: "",
  });
  const [error, setError] = useState({
    userName: false,
    password: false,
  });
  const [debugInfo, setDebugInfo] = useState("");

  useEffect(() => {
    console.log("Component mounted or updated");
    setDebugInfo((prev) => prev + "\nComponent mounted or updated");
  }, []);

  function onLoginChange(event) {
    const { name, value } = event.target;
    console.log(`Input changed: ${name} = ${value}`);
    setDebugInfo((prev) => prev + `\nInput changed: ${name} = ${value}`);

    setLogin((prevLoginData) => {
      const newLoginData = {
        ...prevLoginData,
        [name]: value,
      };
      console.log("New login state:", newLoginData);
      return newLoginData;
    });

    setError((prevError) => {
      const newError = {
        ...prevError,
        [name]: !value,
      };
      console.log("New error state:", newError);
      return newError;
    });
  }

  function onLogin(event) {
    event.preventDefault();
    console.log("Login attempted");
    setDebugInfo((prev) => prev + "\nLogin attempted");

    const newError = {
      userName: !login.userName,
      password: !login.password,
    };
    console.log("New error state:", newError);
    setError(newError);

    if (Object.values(newError).some((fieldError) => fieldError)) {
      console.log("Mandatory details are not filled");
      setDebugInfo((prev) => prev + "\nMandatory details are not filled");
      return;
    }

    // Rest of your login logic here...
    console.log("Proceeding with login...");
    setDebugInfo((prev) => prev + "\nProceeding with login...");
  }

  // Other functions (handleAdminLogin, onForgotPassword, parseJwt) remain the same...

  console.log("Rendering component");
  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onLogin}>
        <h1 className="Login-text">Login</h1>
        <div className="input-group">
          <label>Email ID</label>
          <input
            type="text"
            name="userName"
            autoComplete="off"
            placeholder="Email ID"
            className={`login-userName ${
              error.userName ? "login-input-error" : ""
            }`}
            onChange={onLoginChange}
            value={login.userName}
          />
          {error.userName && (
            <span className="error-text">Please Enter User ID</span>
          )}
        </div>
        <div className="input-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            autoComplete="off"
            placeholder="Password"
            className={`login-password ${
              error.password ? "login-input-error" : ""
            }`}
            onChange={onLoginChange}
            value={login.password}
          />
          {error.password && (
            <span className="error-text">Please Enter Password</span>
          )}
        </div>
        <button className="Login-btn" type="submit">
          Login
        </button>
        <p className="Forgot-password-link">Forgot Password?</p>
      </form>
      <div
        style={{
          whiteSpace: "pre-line",
          marginTop: "20px",
          border: "1px solid black",
          padding: "10px",
        }}
      >
        Debug Info:
        {debugInfo}
      </div>
    </div>
  );
}

export default Login;
