import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../login-page/login.css"; // Keeping the same styles

function ForgotPassword() {
  const navigate = useNavigate();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);

  async function onResetPassword(event) {
    event.preventDefault();

    if (!email || !password || !confirmPassword) {
      setError(true);
      return;
    }

    if (password !== confirmPassword) {
      setError(true);
      return; // Optionally, handle password mismatch
    }

    setError(false);
    const userId = localStorage.getItem("userId");
    const token = localStorage.getItem("authToken"); // Get the JWT token
    // const token = localStorage.getItem("authToken");
    console.log("JWT Token:", token);

    try {
      const response = await axios.put(
        `http://localhost:8080/api/user/${userId}`,
        { email, password }, // Send email and new password
        {
          headers: { Authorization: `Bearer ${token}` }, // Include JWT token
        }
      );

      if (response.status === 200) {
        setSuccess(true);
        console.log("Password updated successfully!");
        const token = response.data.jwt; // Ensure this is set correctly
        localStorage.setItem("authToken", token);
      } else {
        setError(true);
      }
    } catch (err) {
      console.error("Error updating password", err);
      setError(true);
    }
  }
  if (!token) {
    setError("No valid JWT token found");
    return;
  }

  return (
    <div className="Login-page">
      <form className="Login-form" onSubmit={onResetPassword}>
        <h1 className="Login-text">Reset Password</h1>
        <div className="input-group">
          <label>Email ID</label>
          <input
            type="email"
            autoComplete="off"
            placeholder="Email ID"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => setEmail(e.target.value)}
            value={email}
          />
          {error && (
            <span className="error-text">Please Enter Valid Email ID</span>
          )}
        </div>
        <div className="input-group">
          <label>New Password</label>
          <input
            type="password"
            autoComplete="off"
            placeholder="New Password"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => setPassword(e.target.value)}
            value={password}
          />
          {error && <span className="error-text">Please Enter Password</span>}
        </div>
        <div className="input-group">
          <label>Confirm Password</label>
          <input
            type="password"
            autoComplete="off"
            placeholder="Confirm Password"
            className={`login-userName ${error ? "login-input-error" : ""}`}
            onChange={(e) => setConfirmPassword(e.target.value)}
            value={confirmPassword}
          />
        </div>
        {success && (
          <span className="success-text">Password updated successfully!</span>
        )}
        <button className="Login-btn" type="submit">
          Reset Password
        </button>
        <a href="/login" className="Forgot-password-link">
          Back to Login
        </a>
      </form>
    </div>
  );
}

export default ForgotPassword;
