  function handleDeposit(e) {
    e.preventDefault();

    if (account.accBalance < inputAmount || account.accBalance === 0) {
      alert("Insufficient balance");
      return;
    } else if (inputAmount < 0) {
      alert("Please insert a valid amount");
      return;
    }

    // Prepare updated sender's account JSON body explicitly
    const senderAccountBody = {
      accNumber: account.accNumber,
      accBalance: account.accBalance - Number(inputAmount),
      accCreatedAt: account.accCreatedAt,
      accType: account.accType,
      userId: account.userId,
    };

    // Send PUT request for sender's account
    axios
      .put(
        `http://localhost:8080/api/account/${account.accNumber}`,
        senderAccountBody,
        { headers: { "Content-Type": "application/json" } }
      )
      .then((response) => {
        console.log("Sender account updated:", response.data);

        // Now get receiver's account details and update
        axios
          .get(`http://localhost:8080/api/account/acc/${receiverAccNumber}`)
          .then((receiverResponse) => {
            const receiverAccount = receiverResponse.data;

            // Prepare updated receiver account body explicitly
            const receiverAccountBody = {
              accNumber: receiverAccount.accNumber,
              accBalance: receiverAccount.accBalance + Number(inputAmount),
              accCreatedAt: receiverAccount.accCreatedAt,
              accType: receiverAccount.accType,
              userId: receiverAccount.userId,
            };

            // Send PUT request to update receiver's account balance
            return axios.put(
              `http://localhost:8080/api/account/${receiverAccNumber}`,
              receiverAccountBody,
              { headers: { "Content-Type": "application/json" } }
            );
          })
