import React, { useEffect, useState } from "react";
import "../Dashboard/dashboard.css";
import axios from "axios";
import Deposit from "./Deposit";
import FundTransfer from "./FundTransfer";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "../Context/AccountProvider";
import { useContext } from "react";
import "../Dashboard/Transaction/transaction.css";
import Loan from "./Loan";

function Dashboard() {
  const { account, setAccount } = useContext(AccountContext);
  const [loading, setLoading] = useState(true);
  const [userDetail, setUserDetails] = useState({});
  const [depositPopUp, setDepositPopUp] = useState(false);
  const [fundTransferPopUp, setFundTransferPopUp] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [loan, setLoan] = useState({
    userId: localStorage.getItem("userId"),
    userName: "",
    userAddress: "",
    loanType: "",
    amountRequired: 0,
    accountNumber: account ? account.accNumber : "",
    mobileNumber: "",
    loanStatus: "Approved",
  });
  const [loanType, setLoanType] = useState("Home Loan");

  const navigate = useNavigate();

  useEffect(() => {
    const userId = localStorage.getItem("userId");

    if (userId) {
      axios
        .get(`http://localhost:8080/api/account/userid/${userId}`)
        .then((response) => {
          setAccount(response.data);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error fetching account data:", error);
          setLoading(false);
        });

      axios
        .get("http://localhost:8080/api/users/" + userId)
        .then((response) => {
          setUserDetails(response.data);
        })
        .catch((error) => console.log(error));

      axios
        .get("http://localhost:8080/api/transaction")
        .then((response) => {
          setTransactions(response.data); // Store transaction data in state variable
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [setAccount]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!account || Object.keys(account).length === 0) {
    return <div>No account data available.</div>;
  }

  const userId = localStorage.getItem("userId");

  return (
    <div
      className={
        depositPopUp || fundTransferPopUp
          ? "Dashboard dashboard-blur"
          : "Dashboard"
      }
    >
      <div className="page-size">
        <div className="nav-bar">
          <div className="nav-bar-data">
            <button
              className="dashboard"
              onClick={() => {
                navigate("/dashboard/" + { userId });
              }}
            >
              Dashboard
            </button>
            <button className="loan">Loan</button>
            <button className="cards">Cards</button>
            <button onClick={() => setDepositPopUp(true)} className="deposit">
              Deposit
            </button>
            <button className="fund" onClick={() => setFundTransferPopUp(true)}>
              {" "}
              Fund Transfer
            </button>
            <button className="bell">ðŸ•­</button>
            <span className="userName">{userDetail.first_Name}</span>
            <button className="img">ðŸ‘¤</button>
          </div>

          {/* Rest of your dashboard content */}
          <div className="acc-bal"></div>
          <div className="account-test">
            <div className="account-Balance">
              <p className="amount">Account Balance: {account.accBalance}</p>
            </div>
            <div className="acc-det"></div>
            <div className="account">
              <div className="account-Details">
                <div className="user-detail">
                  <p>
                    User Name:{" "}
                    {userDetail.first_Name + " " + userDetail.last_Name}
                  </p>
                  <p>Account Number: {account.accNumber}</p>
                  <p>Account Type: {account.accType}</p>
                  <p>Account Created Date: {account.accCreatedAt}</p>
                </div>
              </div>
            </div>
          </div>
          <div className="transaction">
            <p className="transaction-header">Transaction History</p>
            <table className="transaction-table table">
              <thead>
                <tr>
                  <th className="transaction-table ">Sl No.</th>
                  <th className="transaction-table details">
                    Transaction Details
                  </th>
                  <th className="transaction-table">Amount</th>
                  <th className="transaction-table">Balance</th>
                  <th className="transaction-table">Date</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map((transaction, index) => (
                  <tr key={index + 1} className="transaction-head">
                    <td className="center index">{index + 1}</td>
                    <td className="details center">
                      {transaction.transferDetails}
                    </td>
                    <td className="amount-transfered center">
                      {transaction.amountTransfered}{" "}
                      <span className="credited">cr</span>
                    </td>
                    <td className="acc-balance center">
                      {transaction.balance}
                    </td>
                    <td className="date center">{transaction.transferDate}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
        {depositPopUp && <Deposit setDeposit={setDepositPopUp} />}
        {fundTransferPopUp && (
          <FundTransfer setFundTransfer={setFundTransferPopUp} />
        )}
      </div>
    </div>
  );
}

export default Dashboard;
